import Square from "./Square";
import { useState, useCallback, useEffect } from "react";

export function GameGrid({ size }) {
  const [squares, setSquares] = useState(
    Array.from({ length: size }).map((_, i) =>
      Array.from({ length: size }).map((_, j) => ({
        text: "",
        x: i,
        y: j,
      })),
    ),
  );
  const [currentPlayer, setCurrentPlayer] = useState("X");
  const [gameState, setGameState] = useState("Next player: X");

  useEffect(() => {
    setGameState(`Next player: ${currentPlayer}`);
  }, [currentPlayer]);

  const onClick = useCallback(
    (x, y) => {
      if (squares[x][y].text !== "") return;

      setSquares((prev) => {
        const newSquares = [...prev];
        newSquares[x][y] = {
          ...newSquares[x][y],
          text: currentPlayer,
        };
        return newSquares;
      });
    },
    [squares],
  );

  return (
    <div>
      <div style={style.container}>
        {Array.from({ length: size }).map((_, i) => (
          <div key={i} style={style.row}>
            {Array.from({ length: size }).map((_, j) => (
              <Square
                key={`${i}_${j}`}
                text={squares[i][j].text}
                x={i}
                y={j}
                onClick={onClick}
              />
            ))}
          </div>
        ))}
      </div>
      <div>{gameState}</div>
    </div>
  );
}

function checkForWinner(squares, x, y) {
  if (
    (squares[x][0] === squares[x][1]) === squares[x][2] ||
    (squares[0][y] === squares[1][y]) === squares[2][y]
  ) {
    return "WINNER!";
  } else return null;
}

const style = {
  container: {
    flexDirection: "column",
    gap: 10,
    display: "flex",
  },
  row: {
    flexDirection: "row",
    gap: 10,
    display: "flex",
  },
};
