import Square from "./Square";
import { useState, useCallback, useEffect } from "react";

export function GameGrid({ size }) {
  const [squares, setSquares] = useState(
    Array.from({ length: size }).map((_, i) =>
      Array.from({ length: size }).map((_, j) => ({
        text: string | undefined,
        x: i,
        y: j,
      })),
    ),
  );
  const [currentPlayer, setCurrentPlayer] = useState("X");
  const [gameState, setGameState] = useState("Next player: X");

  useEffect(() => {
    setGameState(`Next player: ${currentPlayer}`);
  }, [currentPlayer]);

  const onClick = useCallback(
    (x, y) => {
      if (squares[x][y].text !== undefined) return;
      setCurrentPlayer((current) => {
        return current === "X" ? "O" : "X";
      });
      setSquares((prev) => {
        const newSquares = [...prev];
        newSquares[x][y] = {
          ...newSquares[x][y],
          text: currentPlayer,
        };

        return newSquares;
      });
    },
    [squares],
  );

  return (
    <div>
      <div style={style.container}>
        {Array.from({ length: size }).map((_, i) => (
          <div key={i} style={style.row}>
            {Array.from({ length: size }).map((_, j) => (
              <Square
                key={`${i}_${j}`}
                text={squares[i][j].text}
                x={i}
                y={j}
                onClick={onClick}
              />
            ))}
          </div>
        ))}
      </div>
      <div>{gameState}</div>
    </div>
  );
}

function checkForWinner() {}

const style = {
  container: {
    flexDirection: "column",
    gap: 10,
    display: "flex",
  },
  row: {
    flexDirection: "row",
    gap: 10,
    display: "flex",
  },
};
