import Square from "./Square";
import { useState } from 'react';

export function GameGrid({ size }) {
    const [squares, setSquares] = useState(Array(size * size).fill(1));
    return(
        <div> 
            {squares.map((square,index) => <Square key={index} value={square}/>)}
        </div>
    )
}
const style = {
    container: {
        flex:1
    }
}
